import React, { Component } from 'react'
import Link from '../components/Link/Link.component'
import './Profile.css'
export default class Profile extends Component {
    constructor(){
        super(); 
        this.state = {
           data : [{}],
           loading : true 
        }
    }
    async componentDidMount(){
        const profile = await fetch('https://api.github.com/users/Abhishekdileep');
        const profileJSON =await  profile.json();
        if(profileJSON){

            this.setState({
                data :profileJSON,
                loading : false

            })

        }
    }
    render() {
        const { data , loading  } = this.state ;
        if(loading){
            return <div>Loading .....</div>
        }
        else{

            return (
                <div className = 'Profile-container'>
                    <img src={data.avatar_url} alt="Avatar" className='Profile-avatar'></img>
        <ul>
            <li><strong>HTML url</strong> <Link url={data.html_url} title="Github URL" /></li>
            <li><strong>repos url</strong> <Link url={data.repos_url} title="Repo URL" /></li>
            <li><strong>Name  </strong> {data.name} </li>
            <li><strong>Location  </strong> {data.location} </li>
            <li><strong>Company  </strong> {data.company} </li>
            <li><strong>Followers  </strong> {data.followers} </li>
            <li className="Bio   "><strong>Bio </strong> {"Hey there"} </li>
        </ul>
            </div>
        )}
    }
}
