import React, { Component } from 'react'

export default class Profile extends Component {
    constructor(){
        super(); 
        this.state = {
           data : [{}],
           loading : true 
        }
    }
    async componentDidMount(){
        const profile = await fetch('https://api.github.com/users/Abhishekdileep');
        const profileJSON = profile.json();
        if(profileJSON){
             console.log(profileJSON , "Is profile.json")
            this.setState({
                data :profileJSON,
                loading : false

            })
            console.log(this.state.data)
        }
    }
    render() {
        const { data , loading  } = this.state ;
        if(loading){
            return <div>Loading .....</div>
        }
        return (
            <div>
                <ul>
        <li>Avatar URL : {data.avatar_url}</li>
        <li>HTML url :{data.html_url}</li>
        <li>repos_url : {data.repos_url}</li>
        <li>name : {data.login}</li>
        <li>company : {data.company}</li>
        <li>location : {data.location} </li>
        <li>email : {data.email} </li>
        <li>bio : {data.bio} </li>
                </ul>
            </div>
        )
    }
}
